// Parse Mobile.de listing data
const item = $input.first().json[0];

if (!item) {
  throw new Error('No listing data found');
}

// Map to standardized format
const vehicleData = {
  make: item.brand || item.make || '',
  model: item.model || '',
  year: parseInt(item.year) || new Date().getFullYear(),
  mileage_km: parseInt(String(item.mileage || item.km || '0').replace(/\D/g, '')),
  price_eur: parseFloat(String(item.price || '0').replace(/[^0-9.,]/g, '').replace(',', '.')),
  fuelType: mapFuelType(item.fuel || item.fuelType || 'petrol'),
  transmission: (item.transmission || '').toLowerCase().includes('automat') ? 'automatic' : 'manual',
  co2_gkm: parseInt(item.co2 || item.co2Emission || '0') || estimateCO2(item),
  firstRegistrationDate: parseRegistrationDate(item.firstRegistration || item.year),
  listingUrl: item.url || $input.first().json.body?.url || '',
  source: 'mobile.de',
  rawData: item
};

function mapFuelType(fuel) {
  const fuelLower = fuel.toLowerCase();
  if (fuelLower.includes('diesel')) return 'diesel';
  if (fuelLower.includes('electr') || fuelLower.includes('elektr')) return 'electric';
  if (fuelLower.includes('hybrid')) return 'hybrid';
  if (fuelLower.includes('lpg') || fuelLower.includes('gas')) return 'lpg';
  return 'petrol';
}

function parseRegistrationDate(dateStr) {
  if (!dateStr) return new Date();

  // Handle MM/YYYY format
  if (dateStr.includes('/')) {
    const [month, year] = dateStr.split('/');
    return new Date(parseInt(year), parseInt(month) - 1, 1);
  }

  // Handle year only
  if (/^\d{4}$/.test(dateStr)) {
    return new Date(parseInt(dateStr), 0, 1);
  }

  return new Date(dateStr);
}

function estimateCO2(item) {
  // Rough CO2 estimation based on engine size if not provided
  const engineCC = parseInt(item.engineSize || item.displacement || '0');
  const fuel = (item.fuel || 'petrol').toLowerCase();

  if (fuel.includes('electr')) return 0;
  if (engineCC < 1000) return 95;
  if (engineCC < 1400) return 115;
  if (engineCC < 1800) return 135;
  if (engineCC < 2500) return 165;
  return 200;
}

return { json: vehicleData };

